function custom_entry_content()
{ ?>
    <div id="main_container">
        <div class="default-page-container ">
            <div class="grid-container">
                <div class="grid-x grid-margin-x list-decor">
                    <div class="cell">
                        <?php get_template_part('parts/core/breadcrumbs', null); ?>
                    </div>

                    <?php if (have_posts()) : ?>
                        <?php while (have_posts()) : the_post(); ?>
                            <div class="cell">
                                <?php /*if (!get_the_post_thumbnail_url(get_the_ID(), 'full_hd')): ?>
                                    <h1><?php the_title() ?></h1>
                                <?php endif;*/ ?>
<!--								<p>By --><?php //the_author_posts_link(); ?><!-- on --><?php //the_time('F jS, Y'); ?><!--  in --><?php //the_category(', '); ?><!-- --><?php //edit_post_link(__('{Edit}'), ''); ?><!--</p>-->
                                <?php add_filter('the_content', 'wpautop'); ?>
                                <?php the_content(); ?>
                            </div>
                        <?php endwhile; ?>
                    <?php endif; ?>
                </div>

            </div>
        </div>
    </div>
<?php }

